import React, { Component } from 'react';
import Particles from 'react-particles-js';
import './App.css';
import Header from '../../containers/Header/Header';
import Footer from '../../containers/Footer/Footer';
import About from '../../containers/About/About';
import Experience from '../../containers/Experience/Experience';
import Modal from 'react-responsive-modal';
import Contact from '../../containers/Contact/Contact';
import Callout from '../../containers/Callout/Callout';

const particleOptions = {
    "particles": {
      "number": {
        "value": 50,
        "density": {
          "enable": true,
          "value_area": 800
        }
      },
      "color": {
        "value": "#ffffff"
      },
      "shape": {
        "type": "circle",
        "stroke": {
          "width": 0,
          "color": "#000000"
        },
        "polygon": {
          "nb_sides": 3
        },
        "image": {
          "src": "img/github.svg",
          "width": 100,
          "height": 100
        }
      },
      "opacity": {
        "value": 0.5,
        "random": false,
        "anim": {
          "enable": false,
          "speed": 1,
          "opacity_min": 0.1,
          "sync": false
        }
      },
      "size": {
        "value": 7.891476416322726,
        "random": true,
        "anim": {
          "enable": false,
          "speed": 0,
          "size_min": 0.1,
          "sync": true
        }
      },
      "line_linked": {
        "enable": false,
        "distance": 150,
        "color": "#ffffff",
        "opacity": 0.4,
        "width": 1
      },
      "move": {
        "enable": true,
        "speed": 6,
        "direction": "none",
        "random": false,
        "straight": false,
        "out_mode": "out",
        "bounce": false,
        "attract": {
          "enable": false,
          "rotateX": 600,
          "rotateY": 1200
        }
      }
    },
    "interactivity": {
      "detect_on": "canvas",
      "events": {
        "onhover": {
          "enable": true,
          "mode": "repulse"
        },
        "onclick": {
          "enable": true,
          "mode": "push"
        },
        "resize": true
      },
      "modes": {
        "grab": {
          "distance": 400,
          "line_linked": {
            "opacity": 1
          }
        },
        "bubble": {
          "distance": 400,
          "size": 40,
          "duration": 2,
          "opacity": 8,
          "speed": 3
        },
        "repulse": {
          "distance": 200,
          "duration": 0.4
        },
        "push": {
          "particles_nb": 4
        },
        "remove": {
          "particles_nb": 2
        }
      }
    },
    "retina_detect": true
  }


class App extends Component {
  constructor() {
    super();

    this.state = {
      showAbout: false
      }
    }

  handleShowAbout= () => {
    const { showAbout } = this.state;
    if (showAbout) {
      this.setState({ showAbout: false });
    }
    else {
      this.setState({ showAbout: true });
    }
    console.log(this.state)
  }


  render() {
    const { showAbout } = this.state;
    return (
      <div className="App bg-black-20">
        <div id="Particle-Canvas">
          <Particles className="particles" params={particleOptions} />
        </div>
        <div>
          <Header handleShowContact={this.handleShowAbout}/>
          <div className="dt vh-75 center">
          <Callout/>

          </div>
        </div>
        <div className="">
          <Experience />
        </div>
      </div>
    );
  }
}

export default App;
